import './App.css'; // Agrega estilos personalizados aquí
import Flag from 'react-world-flags';

// Componente para mostrar la bandera
const FlagComponent = ({ countryCode }) => (
  <div>
    <Flag code={countryCode} style={{ width: '50px', height: '30px' }} />
  </div>
);

// Componente para mostrar la medalla
const Medal = ({ medalType }) => {
  const medalImages = {
    gold: 'https://upload.wikimedia.org/wikipedia/commons/thumb/1/15/Gold_medal.svg/300px-Gold_medal.svg.png',
    silver: 'https://upload.wikimedia.org/wikipedia/commons/thumb/0/03/Silver_medal.svg/300px-Silver_medal.svg.png?20230225094801',
    bronze: 'https://upload.wikimedia.org/wikipedia/commons/thumb/5/52/Bronze_medal.svg/300px-Bronze_medal.svg.png?20230225094756',
  };

  return (
    <img src={medalImages[medalType]} alt={`${medalType} medal`} style={{ width: '30px' }} />
  );
};

// Componente para mostrar la tabla de medallas
const MedalTable = ({ data }) => {
  // Ordenar los datos en función del total de medallas (oro, plata, bronce)
  const sortedData = [...data].sort((a, b) => 
    (b.gold - a.gold) || (b.silver - a.silver) || (b.bronze - a.bronze)
  );

  // Añadir el puesto a los datos
  const dataWithRank = sortedData.map((item, index) => ({
    ...item,
    rank: index + 1,
    total: item.gold + item.silver + item.bronze
  }));

  return (
    <div className="medal-table-container">
      <h2>Medallero Final</h2>
      <table>
        <thead>
          <tr>
            <th>PUESTO</th>
            <th>PAIS</th>
            <th>ORO</th>
            <th>PLATA</th>
            <th>BRONCE</th>
            <th>TOTAL</th>
          </tr>
        </thead>
        <tbody>
          {dataWithRank.map((item) => (
            <tr key={item.countryCode}>
              <td>{item.rank}</td>
              <td><FlagComponent countryCode={item.countryCode} /></td>
              <td><Medal medalType="gold" /> {item.gold}</td>
              <td><Medal medalType="silver" /> {item.silver}</td>
              <td><Medal medalType="bronze" /> {item.bronze}</td>
              <td>{item.total}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Componente para mostrar los atletas destacados en forma de podio
const TopAthletes = ({ athletes }) => (
  <div className="top-athletes-container">
    <h2>Top 3 Atletas Destacados</h2>
    <div className="podium">
      {athletes.map((athlete, index) => (
        <div key={index} className={`podium-item podium-${index + 1}`}>
          <img src={athlete.image} alt={athlete.name} className="athlete-image" />
          <div className="athlete-info">
            <strong>{athlete.name}</strong> - {athlete.sport} ({athlete.country}) - {athlete.medals} Medallas
          </div>
        </div>
      ))}
    </div>
  </div>
);

